refentry.refsect1:
&reftitle.description;
intdbx_compare arrayrow_a arrayrow_b stringcolumn_key intflagsDBX_CMP_ASC | DBX_CMP_NATIVE
dbx_compare - это вспомогательная функция для dbx_sort, позволяющая реализовать пользовательскую сортировку.

  &reftitle.examples;



    Пример использования dbx_compare


<?php
function user_re_order($a, $b) 
{
    $rv = dbx_compare($a, $b, "parentid", DBX_CMP_DESC);
    if (!$rv) {
        $rv = dbx_compare($a, $b, "id", DBX_CMP_NUMBER);
    }
    return $rv;
}

$link   = dbx_connect(DBX_ODBC, "", "db", "username", "password")
    or die("Не получилось подключиться");

$result = dbx_query($link, "SELECT id, parentid, description FROM table ORDER BY id");
    // данные в $result отсортированы по столбцу id

dbx_sort($result, "user_re_order");
    // А теперь в обратном порядке по parentid, а потом уже по id

dbx_close($link);
?>
