<?php
$mysqli = new mysqli("localhost", "my_user", "my_password", "world");

/* Проверяем соединение */
if (mysqli_connect_errno()) {
    printf("Не удалось подключиться: %s\n", mysqli_connect_error());
    exit();
}

$city = "Amersfoort";

/* создаём подготавливаемый запрос */
$stmt =  $mysqli->stmt_init();
if ($stmt->prepare("SELECT District FROM City WHERE Name=?")) {

    /* привязываем переменные к параметрам */
    $stmt->bind_param("s", $city);

    /* выполняем запрос */
    $stmt->execute();

    /* привязываем результаты к переменным */
    $stmt->bind_result($district);

    /* выбираем данные из результата */
    $stmt->fetch();

    printf("%s находится в округе %s\n", $city, $district);

    /* закрываем запрос */
    $stmt->close();
}

/* закрываем соединение */
$mysqli->close();
?>




   &style.procedural;


<?php
$link = mysqli_connect("localhost", "my_user", "my_password", "world");

/* Проверяем соединение */
if (mysqli_connect_errno()) {
    printf("Не удалось подключиться: %s\n", mysqli_connect_error());
    exit();
}

$city = "Amersfoort";

/* создаём подготавливаемый запрос */
$stmt = mysqli_stmt_init($link);
if (mysqli_stmt_prepare($stmt, 'SELECT District FROM City WHERE Name=?')) {

    /* привязываем переменные к параметрам */
    mysqli_stmt_bind_param($stmt, "s", $city);

    /* выполняем запрос */
    mysqli_stmt_execute($stmt);

    /* привязываем результаты к переменным */
    mysqli_stmt_bind_result($stmt, $district);

    /* выбираем данные из результата */
    mysqli_stmt_fetch($stmt);

    printf("%s находится в округе %s\n", $city, $district);

    /* закрываем запрос */
    mysqli_stmt_close($stmt);
}

/* закрываем соединение */
mysqli_close($link);
?>


   &examples.outputs;


Amersfoort находится в округе Utrecht
