refentry.refsect1:
&reftitle.parameters;
pair
Пара цветов, из которой можно получить цвета переднего плана и фона для активных цветов.

  &reftitle.examples;



    Запись строки с указанным цветом на экран


<?php
ncurses_init();

// Если терминал поддерживает цвета, инициализируйте и установите активный цвет
if (ncurses_has_colors()) {
    ncurses_start_color();
    ncurses_init_pair(1, NCURSES_COLOR_YELLOW, NCURSES_COLOR_BLUE);
    ncurses_color_set(1);
}

// Напишите строку в указанном месте
ncurses_mvaddstr(10, 10, "Привет, мир! Жёлтый на синем тексте!");

// Очистить вывод на экране
ncurses_refresh();

ncurses_end();
?>
