refentry.refsect1:
&reftitle.description;
mixedpg_result_status resourceresult inttypePGSQL_STATUS_LONG
pg_result_status возвращает текущее состояние ресурса результата запроса, либо тэг завершения сервером работы с с этим ресурсом.

  &reftitle.examples;



    Пример использования pg_result_status


<?php

// Подключаемся к базе данных
$conn = pg_pconnect("dbname=publisher");

// Выполняем команду COPY
$result = pg_query($conn, "COPY authors FROM STDIN;");

// Получаем состояние результата запроса
$status = pg_result_status($result);

// Разбираем полученные данные
if ($status == PGSQL_COPY_IN)
   echo "Copy began.";
else
   echo "Copy failed.";

?>


    &example.outputs;


Copy began.
